mapper.select:
select
salary_id, staff_id, depart_name, work_days, off_days, deduct_count, insurance_count, allowance_count,
salary_count, comment
from employee_salary


        and salary_id = #{salaryId}


        and staff_id = #{staffId}


        and depart_name = #{departName}


        and work_days = #{workDays}


        and off_days = #{offDays}


        and deduct_count = #{deductCount}


        and insurance_count = #{insuranceCount}


        and allowance_count = #{allowanceCount}


        and salary_count = #{salaryCount}


        and comment = #{comment}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from employee_salary


        and salary_id = #{salaryId}


        and staff_id = #{staffId}


        and depart_name = #{departName}


        and work_days = #{workDays}


        and off_days = #{offDays}


        and deduct_count = #{deductCount}


        and insurance_count = #{insuranceCount}


        and allowance_count = #{allowanceCount}


        and salary_count = #{salaryCount}


        and comment = #{comment}
