mapper.select:
select
id, account_code, file_name, in_time, minio_bucket, minio_path
from minio_files


        and id = #{id}


        and account_code = #{accountCode}


        and file_name = #{fileName}


        and in_time = #{inTime}


        and minio_bucket = #{minioBucket}


        and minio_path = #{minioPath}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from minio_files


        and id = #{id}


        and account_code = #{accountCode}


        and file_name = #{fileName}


        and in_time = #{inTime}


        and minio_bucket = #{minioBucket}


        and minio_path = #{minioPath}
