mapper.select:
select
id, patient_id, case_id, cost_list, cost_count, in_time, out_time
from paitent_cost_detail


        and id = #{id}


        and patient_id = #{patientId}


        and case_id = #{caseId}


        and cost_list = #{costList}


        and cost_count = #{costCount}


        and in_time = #{inTime}


        and out_time = #{outTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from paitent_cost_detail


        and id = #{id}


        and patient_id = #{patientId}


        and case_id = #{caseId}


        and cost_list = #{costList}


        and cost_count = #{costCount}


        and in_time = #{inTime}


        and out_time = #{outTime}
