mapper.select:
select
log_id, log_name, log_module, log_info, log_level, in_time, log_describe
from sys_logs


        and log_id = #{logId}


        and log_name = #{logName}


        and log_module = #{logModule}


        and log_info = #{logInfo}


        and log_level = #{logLevel}


        and in_time = #{inTime}


        and log_describe = #{logDescribe}


limit #{pageable.offset}, #{pageable.pageSize}

select
log_id, log_name, log_module, log_info, log_level, in_time, log_describe
from sys_logs


        and log_id = #{logId}


        and log_name = #{logName}


        and log_module = #{logModule}


        and log_info = #{logInfo}


        and log_level = #{logLevel}


        and in_time = #{inTime}


        and log_describe = #{logDescribe}

select count(1)
from sys_logs


        and log_id = #{logId}


        and log_name = #{logName}


        and log_module = #{logModule}


        and log_info = #{logInfo}


        and log_level = #{logLevel}


        and in_time = #{inTime}


        and log_describe = #{logDescribe}
